import React, { useState, useRef } from "react";
import { MapContainer, TileLayer, Circle, useMap } from "react-leaflet";
import "leaflet/dist/leaflet.css";

// üîÑ Âú∞Âõ≥„ÇíÁßªÂãï„Åï„Åõ„Çã„Åü„ÇÅ„ÅÆ„Ç≥„É≥„Éù„Éº„Éç„É≥„Éà
function MapFlyTo({ coords }) {
  const map = useMap();
  if (coords) {
    map.flyTo(coords, 13);
  }
  return null;
}

function App() {
  const [search, setSearch] = useState("");
  const [coords, setCoords] = useState([35.6812, 139.7671]); // ÂàùÊúüÂÄ§ÔºöÊù±‰∫¨ÈßÖ
  const [dangerZones, setDangerZones] = useState([
    { lat: 35.6812, lng: 139.7671, radius: 500, color: "red" },
    { lat: 35.6895, lng: 139.6917, radius: 300, color: "orange" },
  ]);

  const handleSearch = async (e) => {
    if (e.key === "Enter") {
      const response = await fetch(
        `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(search)}`
      );
      const data = await response.json();
      if (data && data.length > 0) {
        const lat = parseFloat(data[0].lat);
        const lon = parseFloat(data[0].lon);
        setCoords([lat, lon]);
      } else {
        alert("Â†¥ÊâÄ„ÅåË¶ã„Å§„Åã„Çä„Åæ„Åõ„Çì„Åß„Åó„ÅüÔºÅ");
      }
    }
  };

  return (
    <div className="h-screen w-screen flex flex-col">
      <header className="bg-blue-600 text-white text-xl p-4">
        Safety Map
      </header>

      <div className="p-4">
        <input
          type="text"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          onKeyDown={handleSearch}
          placeholder="Search area..."
          className="w-full p-2 border rounded"
        />
      </div>

      <div className="flex-1">
        <MapContainer center={coords} zoom={13} className="h-full w-full">
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
          <MapFlyTo coords={coords} />

          {dangerZones.map((zone, i) => (
            <Circle
              key={i}
              center={[zone.lat, zone.lng]}
              radius={zone.radius}
              pathOptions={{ color: zone.color }}
            />
          ))}
        </MapContainer>
      </div>
    </div>
  );
}

export default App;
