import React, { useState } from "react";
import { MapContainer, TileLayer, Polygon, useMap } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import {
  Home,
  Mail,
  Tag,
  Settings,
  Footprints,
  Search
} from 'lucide-react';

function MapFlyTo({ coords }) {
  const map = useMap();
  if (coords) {
    map.flyTo(coords, 13);
  }
  return null;
}

function App() {
  const [isContactOpen, setIsContactOpen] = useState(false);
  const [language, setLanguage] = useState("English");
  const [isSettingOpen, setIsSettingOpen] = useState(false);
  const [initialPosition, setInitialPosition] = useState("Seattle");
  const [darkMode, setDarkMode] = useState(false);
  const [search, setSearch] = useState("");
  const [coords, setCoords] = useState([47.6062, -122.3321]);
  const seattleCenter = [47.6062, -122.3321];

  const outerBounds = [
    [-90, -180],
    [-90, 180],
    [90, 180],
    [90, -180],
  ];

  const makeCircle = (center, radiusInKm, points = 32) => {
    const [lat, lng] = center;
    const circle = [];
    for (let i = 0; i < points; i++) {
      const angle = (i / points) * 2 * Math.PI;
      const dx = radiusInKm / 111 * Math.cos(angle);
      const dy = radiusInKm / (111 * Math.cos((lat * Math.PI) / 180)) * Math.sin(angle);
      circle.push([lat + dx, lng + dy]);
    }
    return circle;
  };

  const handleSearch = async (e) => {
    if (e.key === "Enter") {
      const response = await fetch(
        `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(search)}`
      );
      const data = await response.json();
      if (data && data.length > 0) {
        const lat = parseFloat(data[0].lat);
        const lon = parseFloat(data[0].lon);
        setCoords([lat, lon]);
      } else {
        alert("場所が見つかりませんでした！");
      }
    }
  };

  return (
    <div className={`${darkMode ? 'dark' : ''}`}>
      <div className="h-screen w-screen flex flex-col font-sans">
        {/* 上部バー */}
        <div className="bg-white shadow-md p-4 flex items-center space-x-3">
          <Footprints className="text-blue-700 w-6 h-6" />
          <h1 className="text-xl font-poppins text-blue-900">
            <span className="font-normal">Her</span>
            <span className="font-bold">Route</span>
          </h1>
        </div>

        {/* 本体レイアウト */}
        <div className="flex flex-1">
          {/* サイドバー */}
          <aside className="bg-blue-700 text-white w-16 flex flex-col items-center py-4 space-y-6">
            <Home className="w-6 h-6" />
            <Tag className="w-6 h-6" />
            <Mail 
              className="w-6 h-6 cursor-pointer"
              nClick={() => {
                setIsSettingOpen(false);
                setIsContactOpen(true);
              }}
            />
            <Settings
             className="w-6 h-6 cursor-pointer"
             onClick={() => {
              setIsSettingOpen(!isSettingOpen);
              setIsContactOpen(false);
             }}
            />
          </aside>

                  {/* コンテンツ部分（全体） */}
                  <main className="flex-1 bg-blue-100 dark:bg-gray-900 p-6 overflow-hidden flex flex-col">
            {/* タイトル + 検索：設定画面のときは非表示 */}
            {!isSettingOpen && (
            <div className="flex justify-between items-center mb-4">
              <div className="flex items-center space-x-4">
                {/* Toggle */}
                <label className="flex items-center cursor-pointer">
                  <input
                    type="checkbox"
                    className="sr-only"
                    checked={darkMode}
                    onChange={() => setDarkMode(!darkMode)}
                  />
                  <div className="w-10 h-5 bg-gray-300 dark:bg-gray-600 rounded-full relative">
                    <div
                      className={`absolute w-5 h-5 bg-white rounded-full shadow transition transform ${
                        darkMode ? "translate-x-5 bg-blue-400" : "translate-x-0"
                      }`}
                    ></div>
                  </div>
                </label>
                <h2
                  className={`text-xl font-semibold ${
                    darkMode ? 'text-white' : 'text-gray-800'
                  }`}
                >
                  {language === "English"
                    ? darkMode
                      ? 'Where are you going at night?'
                      : 'Where are you going during the day?'
                    : darkMode
                      ? '夜はどこ行く？'
                      : 'お昼はどこ行く？'}
                </h2>
              </div>

              <div className="relative w-80">
                <Search className="absolute top-2.5 left-3 w-4 h-4 text-gray-400" />
                <input
                  type="text"
                  value={search}
                  onChange={(e) => setSearch(e.target.value)}
                  onKeyDown={handleSearch}
                  placeholder="Search location..."
                  className="w-full pl-10 pr-4 py-2 rounded border focus:outline-none"
                />
              </div>
            </div>
          )}

            {/* 表示切り替え：地図 or 設定画面 */}
            {isSettingOpen ? (
              <div className="space-y-4">
                {/* 初期位置の変更 */}
                <h2 className={`text-xl font-bold ${darkMode ? 'text-white' : 'text-gray-800'}`}>
                  {language === "日本語" ? "初期位置設定" : "Change of initial position"}
                </h2>
                <select
                  value={initialPosition}
                  onChange={(e) => {
                    const val = e.target.value;
                    setInitialPosition(val);
                    if (val === "Seattle") {
                      setCoords([47.6062, -122.3321]);
                    } else if (val === "Tokyo") {
                      setCoords([35.6812, 139.7671]);
                    }
                  }}
                  className="text-black px-4 py-2 rounded"
                >
                  <option value="Seattle">Seattle</option>
                  <option value="Tokyo">Tokyo</option>
                </select>

                {/* 言語設定 */}
                <h2 className={`text-xl font-bold ${darkMode ? 'text-white' : 'text-gray-800'}`}>
                  {language === "日本語" ? "言語設定" : "Language settings"}
                </h2>
                <select
                  value={language}
                  onChange={(e) => setLanguage(e.target.value)}
                  className="text-black px-4 py-2 rounded"
                >
                  <option value="English">English</option>
                  <option value="日本語">日本語</option>
                </select>
              </div>
            ) : isContactOpen ? (
              <div className="space-y-4">
                <h2 className={`text-xl font-bold ${darkMode ? 'text-white' : 'text-gray-800'}`}>
                  {language === "日本語" ? "お名前" : "Name"}
                </h2>
                <input
                  type="text"
                  className="w-full px-4 py-2 rounded border"
                />

                <h2 className={`text-xl font-bold ${darkMode ? 'text-white' : 'text-gray-800'}`}>
                  {language === "日本語" ? "メールアドレス" : "Email address"}
                </h2>
                <input
                  type="email"
                  className="w-full px-4 py-2 rounded border"
                />

                <h2 className={`text-xl font-bold ${darkMode ? 'text-white' : 'text-gray-800'}`}>
                  {language === "日本語" ? "お問い合わせ内容" : "Message"}
                </h2>
                <textarea
                  rows={6}
                  maxLength={200}
                  className="w-full px-4 py-2 rounded border"
                ></textarea>
              </div>
            ) : (
              <div className="flex-1 rounded overflow-hidden">
                <MapContainer center={coords} zoom={13} className="h-full w-full">
                  <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
                  <MapFlyTo coords={coords} />
                  <Polygon
                    positions={[outerBounds, makeCircle(seattleCenter, 15)]}
                    pathOptions={{ color: "#ccc", fillColor: "#ccc", fillOpacity: 0.6, weight: 0 }}
                  />
                </MapContainer>
              </div>
            )}
          </main>
        </div>
      </div>
    </div>
  );
}

export default App;
