import React, { useState } from "react";
import { MapContainer, TileLayer, useMap } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import {
  Home,
  Mail,
  Folder,
  Settings,
  Footprints
} from 'lucide-react';

function MapFlyTo({ coords }) {
  const map = useMap();
  if (coords) {
    map.flyTo(coords, 13);
  }
  return null;
}

function App() {
  const [search, setSearch] = useState("");
  const [coords, setCoords] = useState([35.6812, 139.7671]);

  const handleSearch = async (e) => {
    if (e.key === "Enter") {
      const response = await fetch(
        `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(search)}`
      );
      const data = await response.json();
      if (data && data.length > 0) {
        const lat = parseFloat(data[0].lat);
        const lon = parseFloat(data[0].lon);
        setCoords([lat, lon]);
      } else {
        alert("場所が見つかりませんでした！");
      }
    }
  };

  return (
    <div className="h-screen w-screen flex flex-col font-sans">
      {/* 上部バー */}
      <div className="bg-white shadow-md p-4 flex justify-between items-center">
        <h1 className="text-lg font-bold">BoardCo</h1>
        <input
          type="text"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          onKeyDown={handleSearch}
          placeholder="Search location..."
          className="border p-2 rounded w-1/3"
        />
      </div>

      {/* 本体レイアウト */}
      <div className="flex flex-1">
        {/* サイドバー */}
        <aside className="bg-blue-700 text-white w-16 flex flex-col items-center py-4 space-y-6">
          <Home className="w-6 h-6" />
          <Mail className="w-6 h-6" />
          <Folder className="w-6 h-6" />
          <Settings className="w-6 h-6" />
        </aside>

        {/* メイン（What's new + 地図） */}
        <main className="flex-1 bg-blue-100 p-4 overflow-hidden">
          <h2 className="text-white text-xl font-semibold mb-4">What's new</h2>

          <div className="h-[calc(100%-2rem)] rounded overflow-hidden">
            <MapContainer center={coords} zoom={13} className="h-full w-full">
              <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
              <MapFlyTo coords={coords} />
            </MapContainer>
          </div>
        </main>
      </div>
    </div>
  );
}

export default App;
